import 'package:flutter/material.dart';class ForgotPasswordScreen extends StatelessWidget {  const ForgotPasswordScreen({super.key});  @override  Widget build(BuildContext context) {    Icon passwordIcon = const Icon(Icons.visibility_off_outlined);    double h = MediaQuery.of(context).size.height;    double w = MediaQuery.of(context).size.width;    return Scaffold(      body: SingleChildScrollView(        physics: const BouncingScrollPhysics(),        child: Padding(          padding: const EdgeInsets.symmetric(            horizontal: 20,            vertical: 10,          ),          child: Column(            crossAxisAlignment: CrossAxisAlignment.start,            children: [              Container(                margin: const EdgeInsets.only(top: 30),                height: h * 0.3734,                width: double.infinity,                decoration: const BoxDecoration(                  image: DecorationImage(                    image: AssetImage("Assets/forget.png"),                    // fit: BoxFit.cover,                  ),                ),              ),              const Text(                "Forgot Password",                style: TextStyle(                  fontWeight: FontWeight.w900,                  fontSize: 35,                ),              ),              const Text(                "Reset your Password and make strong.",                style: TextStyle(                  color: Color.fromRGBO(150, 150, 150, 1),                  fontWeight: FontWeight.w400,                  fontSize: 14,                ),              ),              const SizedBox(                height: 30,              ),              const SizedBox(                height: 10,              ),              TextFormField(                obscureText: true,                decoration: InputDecoration(                  suffixIcon: IconButton(                    icon: passwordIcon,                    onPressed: () {},                  ),                  labelText: "New Password",                  helperStyle: const TextStyle(fontWeight: FontWeight.w100),                  border: const OutlineInputBorder(                    borderRadius: BorderRadius.all(                      Radius.circular(10),                    ),                  ),                ),              ),              const SizedBox(                height: 10,              ),              TextFormField(                obscureText: true,                decoration: InputDecoration(                  suffixIcon: IconButton(                    icon: passwordIcon,                    onPressed: () {},                  ),                  labelText: "Re-Enter Password",                  helperStyle: const TextStyle(fontWeight: FontWeight.w100),                  border: const OutlineInputBorder(                    borderRadius: BorderRadius.all(                      Radius.circular(10),                    ),                  ),                ),              ),              const SizedBox(                height: 30,              ),              ElevatedButton(                style: ButtonStyle(                  shape: MaterialStateProperty.all(                    const RoundedRectangleBorder(                      borderRadius: BorderRadius.all(                        Radius.circular(10),                      ),                    ),                  ),                  backgroundColor: MaterialStateProperty.all(                    const Color.fromRGBO(54, 122, 255, 1),                  ),                ),                onPressed: () {},                child: Container(                  alignment: Alignment.center,                  height: h * 0.0585,                  width: w * 0.872,                  child: const Text(                    "Reset Password",                    style: TextStyle(                      color: Colors.white,                      fontSize: 18,                      fontWeight: FontWeight.w800,                    ),                  ),                ),              ),              const SizedBox(                height: 20,              ),              Row(                children: [                  const Icon(                    Icons.arrow_back_ios,                  ),                  const Text(                    "Back to ",                    style: TextStyle(                      fontSize: 16,                    ),                  ),                  InkWell(                    onTap: () {                      Navigator.pop(context);                    },                    child: Text(                      "Sign in",                      style: Theme.of(context).textTheme.bodyMedium?.copyWith(                          decoration: TextDecoration.underline,                          decorationColor:                              const Color.fromRGBO(54, 122, 255, 1),                          color: const Color.fromRGBO(54, 122, 255, 1),                          fontWeight: FontWeight.w500,                          fontSize: 16),                    ),                  ),                ],              ),            ],          ),        ),      ),    );  }}