import 'package:flutter/material.dart';import 'package:introduction_screen/introduction_screen.dart';import 'package:shared_preferences/shared_preferences.dart';class IntroScreen extends StatelessWidget {  const IntroScreen({super.key});  @override  Widget build(BuildContext context) {    double h = MediaQuery.of(context).size.height;    double w = MediaQuery.of(context).size.width;    return Scaffold(      body: IntroductionScreen(        pages: [          PageViewModel(            title: "Welcome to travel app",            body: "Embark on your"                " next adventure with our travel app! Discover new "                "destinations, plan seamless journeys, and create unforgettable"                " memories. Welcome to a world of endless exploration!",            image: Image.asset(              "Assets/intro1.png",              height: h * 0.2934,              width: w * 0.6361,            ),          ),          PageViewModel(            title: "Select Your Resting place ",            body:                "Indulge in tranquility and comfort. Choose your perfect resting place with us, where serenity meets luxury. Select a haven that resonates with your soul. Your ultimate relaxation awaits.",            image: Image.asset(              "Assets/intro2.png",              height: h * 0.2934,              width: w * 0.6361,            ),          ),          PageViewModel(            title: "Enjoy Your Trip",            body:                "Embrace the joy of travel! Explore new horizons, savor every moment, and create lasting memories. Your journey begins here. Enjoy the trip!",            image: Image.asset(              "Assets/intro3.png",              height: h * 0.2934,              width: w * 0.6361,            ),          ),        ],        showSkipButton: true,        showNextButton: true,        showDoneButton: true,        back: const Text("Previous"),        skip: const Text("Skip Intro"),        next: const Text("Next"),        done: const Text("Finish"),        onDone: () async {          SharedPreferences preferences = await SharedPreferences.getInstance();          preferences.setBool('isIntroVisited', true);          Navigator.pushReplacementNamed(context, 'login');        },      ),    );  }}